query.search:
search SupportDetail with value(182 * 864000000000) as six_months_ns, value( ( // Passed (highest → lowest precedence) retirement_date and (retirement_date < currentTime() and 'EOL Exceeded') or end_security_support_date and (end_security_support_date < currentTime() and 'EOSS Exceeded') or end_ext_support_date and (end_ext_support_date < currentTime() and 'EOES Exceeded') or end_support_date and (end_support_date < currentTime() and 'EOS Exceeded') or
// < 6 months away retirement_date and (retirement_date < currentTime() + @six_months_ns and 'EOL less than 6 months away') or end_security_support_date and (end_security_support_date < currentTime() + @six_months_ns and 'EOSS less than 6 months away') or end_ext_support_date and (end_ext_support_date < currentTime() + @six_months_ns and 'EOES less than 6 months away') or end_support_date and (end_support_date < currentTime() + @six_months_ns and 'EOS less than 6 months away') or
// ≥ 6 months away (retirement_date and 'EOL more than 6 months away') or (end_security_support_date and 'EOSS more than 6 months away') or (end_ext_support_date and 'EOES more than 6 months away') or (end_support_date and 'EOS more than 6 months away') ) ) as lifecycle_risk where retirement_date or end_support_date or end_ext_support_date or end_security_support_date and nodecount(traverse HardwareDetail:SupportDetail:ElementWithDetail:) > 0 show explode(#HardwareDetail:SupportDetail:ElementWithDetail:.name) as "Device Name", name as "Model Name", kind(#HardwareDetail:SupportDetail:ElementWithDetail:) as "Node Kind", (retirement_date and formatTime(retirement_date, '%Y-%m-%d')) as 'End of Life', (end_support_date and formatTime(end_support_date, '%Y-%m-%d')) as 'End of Support', (end_ext_support_date and formatTime(end_ext_support_date, '%Y-%m-%d')) as 'End of Ext Support', (end_security_support_date and formatTime(end_security_support_date, '%Y-%m-%d')) as 'End of Security Support', @lifecycle_risk as 'Lifecycle Risk'

search Host
  with
    value(#ElementWithDetail:SupportDetail:OSDetail:SupportDetail.retirement_date)           as retirement_date,
    value(#ElementWithDetail:SupportDetail:OSDetail:SupportDetail.end_support_date)         as end_support_date,
    value(#ElementWithDetail:SupportDetail:OSDetail:SupportDetail.end_ext_support_date)     as end_ext_support_date,
    value(182 * 864000000000)                                                                as six_months_ns,
    value(
      (
        @end_ext_support_date and (@end_ext_support_date < currentTime()                      and 'EOES Exceeded')    or
        @end_support_date     and (@end_support_date     < currentTime()                      and 'EOS Exceeded')     or
        @retirement_date      and (@retirement_date      < currentTime()                      and 'EOL Exceeded')     or
        @retirement_date      and (@retirement_date      < currentTime() + @six_months_ns     and 'EOL less than 6 months away') or
        @end_support_date     and (@end_support_date     < currentTime() + @six_months_ns     and 'EOS less than 6 months away') or
        @end_ext_support_date and (@end_ext_support_date < currentTime() + @six_months_ns     and 'EOES less than 6 months away') or
        (@retirement_date and 'EOL more than 6 months away')  or
        (@end_support_date and 'EOS more than 6 months away')  or
        (@end_ext_support_date and 'EOES more than 6 months away')
      )
    ) as lifecycle_risk
  where
    @retirement_date or @end_support_date or @end_ext_support_date
  order by
    @lifecycle_risk
  show
    name,
    (@retirement_date      and formatTime(@retirement_date,      '%Y-%m-%d')) as 'End of Life',
    (@end_support_date     and formatTime(@end_support_date,     '%Y-%m-%d')) as 'End of Support',
    (@end_ext_support_date and formatTime(@end_ext_support_date, '%Y-%m-%d')) as 'End of Ext Support',
    @lifecycle_risk        as 'Lifecycle Risk',
  taxonomy 'summary_no_name'
